angular.module("contactsApp",["ionic"]).controller("MainCtrl",function($scope,$ionicScrollDelegate,filterFilter){var letters=$scope.letters=[];var contacts=$scope.contacts=[];var currentCharCode="A".charCodeAt(0)-1;window.CONTACTS.sort(function(a,b){return a.last_name>b.last_name?1:-1}).forEach(function(person){var personCharCode=person.last_name.toUpperCase().charCodeAt(0);var difference=personCharCode-currentCharCode;for(var i=1;i<=difference;i++){addLetter(currentCharCode+i)}currentCharCode=personCharCode;contacts.push(person)});for(var i=currentCharCode+1;i<="Z".charCodeAt(0);i++){addLetter(i)}function addLetter(code){var letter=String.fromCharCode(code);contacts.push({isLetter:true,letter:letter});letters.push(letter)}$scope.getItemHeight=function(item){return item.isLetter?40:100};$scope.scrollBottom=function(){$ionicScrollDelegate.scrollBottom(true)};var letterHasMatch={};$scope.getContacts=function(){letterHasMatch={};return contacts.filter(function(item){var itemDoesMatch=!$scope.search||item.isLetter||item.first_name.toLowerCase().indexOf($scope.search.toLowerCase())>-1||item.last_name.toLowerCase().indexOf($scope.search.toLowerCase())>-1;if(!item.isLetter&&itemDoesMatch){var letter=item.last_name.charAt(0).toUpperCase();letterHasMatch[letter]=true}return itemDoesMatch}).filter(function(item){if(item.isLetter&&!letterHasMatch[item.letter]){return false}return true})};$scope.clearSearch=function(){$scope.search=""}});